import React, { useState, useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { 
    Button, 
    Modal, 
    ModalHeader, 
    ModalBody, 
    ModalFooter, 
    Form, 
    FormGroup,
    Label,
    Input, 
    InputGroup 
} from 'reactstrap';
import { useLocation } from 'react-router-dom';

import '../../styles/sidebar.scss';

const AddEmployeeBtn = () => {
    const [jobTitles, setJobTitles] = useState([]);
    const [hidden, setHidden] = useState(false);
    const [isOpen, setIsOpen] = useState(false);
    const getJobTitles = useSelector(state => state.getJobTitlesReducer.jobTitles);
    const location = useLocation();

    useEffect(() => {
        changeHidden(location);
    }, [location]);

    useEffect(() => {
        setJobTitles(getJobTitles);
        console.log(jobTitles)
    }, [getJobTitles]);

    const changeHidden = location => {
        if(location.pathname === '/e_dashboard/employees' || location.pathname === '/e_dashboard/employees/') {
            setHidden(true);
        } else {
            setHidden(false);
        };
    };

    const toggle = () => {
        setIsOpen(!isOpen);
    };

    const closeBtn = <Button className="close" onClick={toggle}>&times;</Button>;

    return (
        <div className="add-employee-div">
            {hidden ? <Button color="success" onClick={toggle}>Add Employee</Button> : null}
            <Modal isOpen={isOpen} toggle={toggle}>
                <ModalHeader toggle={toggle} close={closeBtn}>Add Employee</ModalHeader>
                <ModalBody>
                   <Form>
                       <div className="name-div">
                           <FormGroup>
                               <Label for="firstName">First Name</Label>
                               <Input type="text" name="firstName" id="firstName" />
                           </FormGroup>
                           <FormGroup>
                               <Label for="lastName">Last Name</Label>
                               <Input type="text" name="lastName" id="lastName" />
                           </FormGroup>
                       </div>
                       <div className="job-info-div">
                           <FormGroup>
                               <Label for="jobTitle">Job Title</Label>
                               <Input type="select" name="jobTitle" id="jobTitle">
                                   {jobTitles ? jobTitles.map(jobTitle => (
                                       <option>{jobTitle.jobTitle}</option>
                                   )) : null}
                               </Input>
                           </FormGroup>
                       </div>
                   </Form>
                </ModalBody>
            </Modal> 
        </div>
    );
};

export default AddEmployeeBtn;
