import React, { useState, useEffect } from 'react';
import { useSelector } from 'react-redux';

const TasksListContent = () => {
    const [tasks, setTasks] = useState([]);
    const getTasks = useSelector(state => state.getTasksReducer.tasks);

    useEffect(() => {
        setTasks(getTasks.tasks);
    }, [getTasks]);

    return (
        <div>
        <h1>Tasks</h1>
        {tasks ?
            tasks.map(task => (
                <div>
                    <h3>{task.taskDescription}</h3>
                    <p>{task.taskDate}</p>
                    <p>{task.taskTime}</p>
                </div>
            )) :
            null
        }
        </div>
    );
};

export default TasksListContent;
