import React, { useState, useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { useLocation } from 'react-router-dom';
import EmployeeNavbar from './EmployeeNavbar';
import EmployeeSidebar from './EmployeeSidebar';
import Footer from './Footer';
import { getWorkOrder } from '../../actions/getWorkOrder';

import '../../styles/employee-dashboard.scss';

const WorkOrderPage = () => {
    const [workOrder, setWorkOrder] = useState({});
    const [customer, setCustomer] = useState({});
    const [employees, setEmployees] = useState([]);
    const getWorkOrderFromState = useSelector(state => state.getWorkOrderReducer.workOrder);
    const getUsers = useSelector(state => state.getWorkOrderReducer.users);
    const dispatch = useDispatch();
    const location = useLocation();

    useEffect(() => {
        const pathArray = location.pathname.split('/');
        const jobId = pathArray[pathArray.length - 1];
        dispatch(getWorkOrder(jobId));
    }, []);

    useEffect(() => {
        if(getWorkOrder) {
            setWorkOrder(getWorkOrderFromState);
        };
    }, [getWorkOrderFromState]);

    useEffect(() => {
        let crewMembers = [];

        if(getUsers) {
            console.log(getUsers)
            getUsers.forEach(user => {
                if(user.userType === 'customer') {
                    setCustomer(customer);
                } else {
                    crewMembers.push(user);
                };
            });
            setEmployees(crewMembers);
        }
    }, [getUsers]);

    return (
        <div>
            <EmployeeNavbar />
            <div className="work-order-page">
                <EmployeeSidebar />
                <div className="work-order-page-content">
                    <div className="work-order-info">
                        <p>Customer Name:</p>
                        <p>{`${customer.firstName} ${customer.lastName}`}</p>
                    </div>
                </div>
            </div>
            <Footer />
        </div>
    );
};

export default WorkOrderPage;
