import React, { useState, useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { withRouter } from 'react-router-dom';
import {
    Navbar,
    NavbarBrand,
    Nav,
    NavItem,
    NavLink,
    Button,
    Modal,
    Form,
    ModalHeader,
    FormGroup,
    Label,
    Input
} from 'reactstrap';
import Logo from '../../assets/logo.png';
import UserIcon from '../../assets/user.svg';
import axiosWithAuth from '../Login/withAuth';
import jwt from 'jsonwebtoken';
import { dbUrl } from '../../actions/index';
import Sidebar from './EmployeeSidebar';
import Footer from '../Other/Footer';
import '../../styles/employee-navbar.scss';

const EmployeeNavbar = () => {
    const [tasksActive, setTasksActive] = useState(true);
    const [workOrdersActive, setWorkOrdersActive] = useState(false);
    const [customersActive, setCustomersActive] = useState(false);
    const [filesActive, setFilesActive] = useState(false);
    const [employeesActive, setEmployeesActive] = useState(false);

    const route = (route, e) => {
        e.preventDefault();
        this.props.history.push(`/e_dashboard${route}`);
    }

    const openTab = e => {
        console.log(e.target.name);
    }

    return (
        <div className="nav-container">
            <Navbar className="employee-navbar" expand="md">
                <NavbarBrand href="/">
                    <div className="logo-box">
                        <img className="logo" src={ Logo } alt="rocky mountain lifts" />
                    </div>
                </NavbarBrand>
                <Nav className="ml-auto" navbar>
                    <NavItem>
                        <NavLink className="nav-link" name="tasks" onClick={(e) => openTab(e)} activeClassName="active" to="/">
                            Tasks
                        </NavLink>
                    </NavItem>
                    <NavItem>
                        <NavLink className="nav-link" name="workorders" onClick={(e) => openTab(e)} activeClassName="active" href="/e_dashboard/workorders">
                            Work Orders
                        </NavLink>
                    </NavItem>
                    <NavItem>
                        <NavLink className="nav-link" onClick={(e) => route('/customers', e)}>
                            Customers
                        </NavLink>
                    </NavItem>
                    <NavItem>
                        <NavLink className="nav-link" onClick={(e) => route('/files', e)}>
                            Files
                        </NavLink>
                    </NavItem>
                    <NavItem>
                        <NavLink className="nav-link" onClick={(e) => route('/employees', e)}>
                            Employees
                        </NavLink>
                    </NavItem>
                    <NavItem>
                        <div className="divider" />
                    </NavItem>
                    <NavItem>
                        <NavLink className="nav-link"> 
                            USER 
                        </NavLink> 
                    </NavItem>
                </Nav>
            </Navbar>
        </div>
    )
}

export default withRouter(EmployeeNavbar);
