import React from "react";
import { useSelector, useDispatch } from "react-redux";
import { toggleCalendarView, toggleListView } from "../../actions/taskToggle";
import { Button, ButtonGroup } from "reactstrap";

import "../../styles/sidebar.scss";

const TaskToggle = () => {
  const calendarView = useSelector(
    (state) => state.taskToggleReducer.calendarView
  );
  const dispatch = useDispatch();

  return (
    <div className="task-btns">
      <ButtonGroup>
        <Button
          onClick={() => dispatch(toggleListView())}
          active={calendarView === false}
        >
          List
        </Button>
        <Button
          onClick={() => dispatch(toggleCalendarView())}
          active={calendarView === true}
        >
          Calendar
        </Button>
      </ButtonGroup>
    </div>
  );
};

export default TaskToggle;
