import {
    GET_INSPECTION_FORM_START,
    GET_INSPECTION_FORM_SUCCESS,
    GET_INSPECTION_FORM_FAILURE,
  } from "../actions";

  const initialState = {
    url: null,
    gettingForm: false,
    error: null,
  };

  export const getInpsectionFormReducer = (state = initialState, action) => {
    switch (action.type) {
      case GET_INSPECTION_FORM_START:
        return {
          ...state,
          gettingForm: true,
        };
      case GET_INSPECTION_FORM_SUCCESS:
        return {
          ...state,
          gettingForm: false,
          url: action.payload,
        };
      case GET_INSPECTION_FORM_FAILURE:
        return {
          ...state,
          gettingForm: false,
          error: action.payload,
        };
      default:
        return state;
    }
  };
