import React, { useState, useEffect } from 'react';
import { useSelector } from 'react-redux';
import EmployeeTable from './EmployeeTable';

import '../../styles/employee-dashboard.scss';

const AllEmployeesContent = () => {
    const [employees, setEmployees] = useState([]);
    const getEmployees = useSelector(state => state.getEmployeesReducer.employees);

    useEffect(() => {
        setEmployees(getEmployees.employees);
    }, [getEmployees]);

    return (
        <div className="employees-page">
            <h1>All Employees</h1>
            <EmployeeTable props={employees} />
        </div>
    );
};

export default AllEmployeesContent;
