import React, { useState, useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { withRouter } from 'react-router-dom';
import {
    Navbar,
    NavbarBrand,
    Nav,
    NavItem,
    NavLink,
    Button,
    Modal,
    Form,
    ModalHeader,
    FormGroup,
    Label,
    Input
} from 'reactstrap';
import Logo from '../../assets/logo.png';
import UserIcon from '../../assets/user.svg';
import axiosWithAuth from '../Login/withAuth';
import jwt from 'jsonwebtoken';
import { dbUrl } from '../../actions/index';
import Sidebar from './EmployeeSidebar';
import Footer from '../Other/Footer';
import '../../styles/employee-navbar.scss';

const EmployeeNavbar = props => {
    const [tasksActive, setTasksActive] = useState(true);
    const [workOrdersActive, setWorkOrdersActive] = useState(false);
    const [customersActive, setCustomersActive] = useState(false);
    const [filesActive, setFilesActive] = useState(false);
    const [employeesActive, setEmployeesActive] = useState(false);

    const route = (route, e) => {
        e.preventDefault();
        this.props.history.push(`/e_dashboard${route}`);
    }

    const openTab = e => {
        const name = e.target.name;

        if(name === "tasks") {
            setTasksActive(true);
            setWorkOrdersActive(false);
            setCustomersActive(false);
            setFilesActive(false);
            setEmployeesActive(false);
        }
        else if(props.name === "workorders") {
            setTasksActive(false);
            setWorkOrdersActive(true);
            setCustomersActive(false);
            setFilesActive(false);
            setEmployeesActive(false);
        }
        else if(name === "customers") {
            setTasksActive(false);
            setWorkOrdersActive(false);
            setCustomersActive(true);
            setFilesActive(false);
            setEmployeesActive(false);
        }
        else if(name === "files") {
            setTasksActive(false);
            setWorkOrdersActive(false);
            setCustomersActive(false);
            setFilesActive(true);
            setEmployeesActive(false);
        }
        else if(name === "employees") {
            setTasksActive(false);
            setWorkOrdersActive(false);
            setCustomersActive(false);
            setFilesActive(false);
            setEmployeesActive(true);
        }
    }

    return (
        <div className="nav-container">
            <Navbar className="employee-navbar" expand="md">
                <NavbarBrand href="/">
                    <div className="logo-box">
                        <img className="logo" src={ Logo } alt="rocky mountain lifts" />
                    </div>
                </NavbarBrand>
                <Nav className="ml-auto" navbar>
                    <NavItem>
                        <NavLink className={`nav-link ${tasksActive ? "active" : ""}`} name="tasks" onClick={(e) => openTab(e)} to="/">
                            Tasks
                        </NavLink>
                    </NavItem>
                    <NavItem>
                        <NavLink className={`nav-link ${workOrdersActive ? "active" : ""}`} name="workorders" onClick={(e) => openTab(e)} href="/e_dashboard/workorders">
                            Work Orders
                        </NavLink>
                    </NavItem>
                    <NavItem>
                        <NavLink className={`nav-link ${customersActive ? "active" : ""}`} name="customers" onClick={(e) => openTab(e)} to="/customers">
                            Customers
                        </NavLink>
                    </NavItem>
                    <NavItem>
                        <NavLink className={`nav-link ${filesActive ? "active" : ""}`} name="files" onClick={(e) => openTab(e)} to="/files">
                            Files
                        </NavLink>
                    </NavItem>
                    <NavItem>
                        <NavLink className={`nav-link ${employeesActive ? "active" : ""}`} name="employees" onClick={(e) => openTab(e)} to="/employees">
                            Employees
                        </NavLink>
                    </NavItem>
                    <NavItem>
                        <div className="divider" />
                    </NavItem>
                    <NavItem>
                        <NavLink className="nav-link"> 
                            USER 
                        </NavLink> 
                    </NavItem>
                </Nav>
            </Navbar>
        </div>
    )
}

export default withRouter(EmployeeNavbar);
