import React, { useState, useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { toggleCalendarView, toggleListView } from '../../actions/taskToggle';
import { useLocation } from 'react-router-dom';
import { Button, ButtonGroup } from 'reactstrap';

import '../../styles/sidebar.scss';

const TaskToggle = () => {
    const [hidden, setHidden] = useState(false);
    const calendarView = useSelector(state => state.taskToggleReducer.calendarView);
    const dispatch = useDispatch();
    let location = useLocation();

    useEffect(() => {
        changeHidden(location);
    }, [location]);

    const changeHidden = location => {
        if(location.pathname === '/e_dashboard' || location.pathname === '/e_dashboard/') {
            setHidden(true);
        } else {
            setHidden(false);
        }
    };

    return (
        <div className="task-btns">
            {hidden ? <ButtonGroup className="button-group">
                <Button onClick={() => dispatch(toggleListView())} active={calendarView === false}>List</Button>
                <Button onClick={() => dispatch(toggleCalendarView())} active={calendarView === true}>Calendar</Button>
            </ButtonGroup> : null}
        </div>
    );
};

export default TaskToggle;
