import React, { useState, useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import { Card, CardHeader, CardBody, Table } from "reactstrap";
import { getWorkOrders } from "../../actions/getWorkOrders";

// import "../../styles/employee-dashboard.scss";

const TasksListContent = () => {
  const [tasks, setTasks] = useState([]);
  const getTasks = useSelector((state) => state.getTasksReducer.tasks);
  const [workOrders, setWorkOrders] = useState([]);
  const fetchWorkOrders = useSelector(
    (state) => state.getWorkOrdersReducer.workOrders
  );
  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(getWorkOrders());
  }, []);

  useEffect(() => {
    setTasks(getTasks.tasks);
  }, [getTasks]);

  const dateConverter = (date) => {
    const dateArray = date.split("-");
    return `${dateArray[1]}/${dateArray[2]}/${dateArray[0]}`;
  };

  const timeConverter = (time) => {
    const timeArray = time.split(":");
    const hour = parseInt(timeArray[0]);
    let m;

    if (hour <= 11) {
      m = "AM";
    } else {
      m = "PM";
    }

    if (hour === 0) {
      const newHour = "12";
      timeArray[0] = newHour;
    } else if (hour <= 12) {
      const newHour = hour.toString();
      timeArray[0] = newHour;
    } else {
      const newHour = (hour - 12).toString();
      timeArray[0] = newHour;
    }

    return `${timeArray[0]}:${timeArray[1]} ${m}`;
  };

  return (
    <div className="tasks-page">
      <h1>Tasks</h1>
      {tasks
        ? tasks.map((task) => (
            <Card className="tasks-page-task-card">
              <Table borderless>
                <thead>
                  <tr>
                    <th>Task</th>
                    <th>Date</th>
                    <th>Time</th>
                  </tr>
                </thead>
                <tbody>
                  <tr>
                    <td>{task.taskDescription}</td>
                    <td>{dateConverter(task.taskDate)}</td>
                    <td>{timeConverter(task.taskTime)}</td>
                  </tr>
                </tbody>
              </Table>
            </Card>
          ))
        : null}
    </div>
  );
};

export default TasksListContent;
