import {
    LOGIN_START,
    LOGIN_SUCCESS,
    LOGIN_FAILED
} from '../actions';

const initialState = {
    loggingIn: false,
    id: null,
    userType: null,
    error: false,
    success: false
};

export const loginReducer = (state = initialState, action) => {
    switch(action.type) {
        case LOGIN_START:
            return {
                ...state,
                loggingIn: true
            }
        case LOGIN_SUCCESS:
            console.log(action.payload.response.userType)
            return {
                ...state,
                id: action.payload.userId,
                userType: action.payload.response.userType,
                loggingIn: false,
                success: true
            }
        case LOGIN_FAILED:
            return {
                ...state,
                error: action.payload,
                logginIn: false
            }
        default: return state;
    }
}
