import React, { useState, useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import {
    Card,
    CardHeader,
    CardBody,
    CardTitle,
    CardText,
    Table,
    Modal,
    ModalHeader,
    ModalBody,
    ModalFooter,
    Form,
    FormGroup,
    FormFeedback,
    Label,
    Input,
    Button
} from 'reactstrap';
import { getWorkOrder } from '../../actions/getWorkOrder';

import '../../styles/employee-dashboard.scss';

const OpenWorkOrders = () => {
    const [workOrders, setWorkOrders] = useState([]);
    const [curWorkOrder, setCurWorkOrder] = useState({});
    const getWorkOrders = useSelector(state => state.getWorkOrdersReducer.workOrders);
    const users = useSelector(state => state.getWorkOrderReducer.users);
    const [isOpen, setIsOpen] = useState(false);
    const dispatch = useDispatch();

    useEffect(() => {
        setWorkOrders(getWorkOrders.workOrders);
    }, [getWorkOrders]);

    const dateConverter = date => {
        const dateArray = date.split('-');

        return `${dateArray[1]}/${dateArray[2]}/${dateArray[0]}`;
    };

    const timeConverter = time => {
        const timeArray = time.split(':');
        const hour = parseInt(timeArray[0]);
        let m;

        if(hour <= 11) {
            m = 'AM';
        } else {
            m = 'PM';
        };

        if(hour <= 12) {
            const newHour = hour.toString();
            timeArray[0] = newHour;
        } else {
            const newHour = (hour - 12).toString();
            timeArray[0] = newHour;
        };

        return `${timeArray[0]}:${timeArray[1]} ${m}`;
    };

    const toggle = (e, workOrder) => {
        if(!isOpen) {
            setCurWorkOrder(workOrder);
        } else {
            setCurWorkOrder({});
        }
        setIsOpen(!isOpen);
    };

    useEffect(() => {
        if(curWorkOrder) {
            dispatch(getWorkOrder(curWorkOrder));
        };
    }, [curWorkOrder]);

    return (
        <div className="work-orders-page">
            <h1 className="open-work-orders-title">Open Work Orders</h1>
            {workOrders ? 
                workOrders.map(workOrder => (
                    <Card className="work-order-card" onClick={(e) => toggle(e, workOrder)}>
                        <CardBody>
                        <Table borderless>
                            <thead>
                                <tr>
                                    <th>Job #</th>
                                    <th>Customer Name</th>
                                    <th>Service Type</th>
                                    <th>Service Date</th>
                                    <th>Service Time</th>
                                </tr>
                            </thead>
                            <tbody>
                                <tr>
                                    <th>{workOrder.jobNumber}</th>
                                    <td>{workOrder.customerName}</td>
                                    <td>{workOrder.serviceType.length > 1 ? 'Multi' : workOrder.serviceType}</td>
                                    <td>{dateConverter(workOrder.serviceDate)}</td>
                                    <td>{timeConverter(workOrder.serviceTime)}</td>
                                </tr>
                            </tbody>
                        </Table>
                        </CardBody>
                    </Card>
                ))
            : null}
            <Modal isOpen={isOpen} toggle={toggle}>
                <ModalHeader toggle={toggle}>{`Work Order #: ${curWorkOrder.jobNumber}`}</ModalHeader>
                <ModalBody>
                    <Form>
                        <p>{curWorkOrder.customer}</p>
                    </Form>
                </ModalBody>
            </Modal>
        </div>
    );
};

export default OpenWorkOrders;
