import React, { useState, useEffect } from 'react';
import { useSelector } from 'react-redux';
import {
    Card,
    CardBody,
    CardTitle,
    CardText
} from 'reactstrap';

const OpenWorkOrders = () => {
    const [workOrders, setWorkOrders] = useState([]);
    const getWorkOrders = useSelector(state => state.getWorkOrdersReducer.workOrders);

    useEffect(() => {
        setWorkOrders(getWorkOrders.workOrders);
    }, [getWorkOrders]);

    console.log(workOrders)

    return (
        <div>
            {workOrders ? 
                workOrders.map(workOrder => (
                    <p>{workOrder.jobNumber}</p>
                ))
            : null}
        </div>
    );
};

export default OpenWorkOrders;
