import {
    SUBMIT_WORK_ORDER_REVIEW_START,
    SUBMIT_WORK_ORDER_REVIEW_SUCCESS,
    SUBMIT_WORK_ORDER_REVIEW_FAILURE
} from '../actions';

const initialState = {
    submitting: false,
    success: false,
    errors: null
};

export const workOrderReviewSubmitReducer = (state = initialState, action) => {
    switch(action.type) {
        case SUBMIT_WORK_ORDER_REVIEW_START:
            return {
                ...state,
                submitting: true
            };
        case SUBMIT_WORK_ORDER_REVIEW_SUCCESS:
            return {
                ...state,
                submitting: false,
                success: true
            };
        case SUBMIT_WORK_ORDER_REVIEW_FAILURE:
            return {
                ...state,
                submitting: false,
                errors: action.payload
            };
        default: return state;
    };
};
