import React, { useState, useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import { toggleCalendarView, toggleListView } from "../../actions/taskToggle";
import { useLocation } from "react-router-dom";
import { Button, ButtonGroup } from "reactstrap";

import "../../styles/sidebar.scss";

const TaskToggle = () => {
  const [hidden, setHidden] = useState(true);
  const calendarView = useSelector(
    (state) => state.taskToggleReducer.calendarView
  );
  const dispatch = useDispatch();
  const location = useLocation();

  useEffect(() => {
    changeHidden(location);
  }, [location]);

  const changeHidden = (location) => {
    if (
      location.pathname === "/dashboard" ||
      location.pathname === "/dashboard/"
    ) {
      setHidden(false);
    } else {
      setHidden(true);
    }
  };

  return (
    <div className="task-btns">
      {hidden ? null : (
        <ButtonGroup className="button-group">
          <Button
            onClick={() => dispatch(toggleListView())}
            active={calendarView === false}
          >
            List
          </Button>
          <Button
            onClick={() => dispatch(toggleCalendarView())}
            active={calendarView === true}
          >
            Calendar
          </Button>
        </ButtonGroup>
      )}
    </div>
  );
};

export default TaskToggle;
