import React, { useState, useEffect, useRef } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import {
    Button,
    Modal,
    ModalHeader,
    ModalBody,
    ModalFooter,
    Form,
    FormGroup,
    FormFeedback,
    Label,
    Input,
} from 'reactstrap';
import Select from 'react-select';
import { useLocation } from 'react-router-dom';

import '../../styles/sidebar.scss';

const AddWorkOrderBtn = () => {
    const [workOrder, setWorkOrder] = useState({
        customer: '',
        serviceDate: '',
        serviceTime: '',
        serviceDescription: '',
        serviceType: '',
        crewMembers: [],
        prevJobNumber: undefined,
        notes: '',
        officeNotes: '',
        quote: ''
    });
    const [errors, setErrors] = useState({});
    const [success, setSuccess] = useState(false);
    const getErrors = useSelector(state => state);
    const getSuccess = useSelector(state => state);
    const [employees, setEmployees] = useState([]);
    const employeesArray = [];
    const [customers, setCustomers] = useState([]);
    const customersArray = [];
    const getUsers = useSelector(state => state.getUsersReducer.users); 
    const [hidden, setHidden] = useState(false);
    const [isOpen, setIsOpen] = useState(false);
    const dispatch = useDispatch();
    const location = useLocation();

    useEffect(() => {
        changeHidden(location);
    }, [location]);

    useEffect(() => {
        if(getUsers.users) {
            getUsers.users.forEach(user => {
                if(user.userType === 'employee') {
                    employeesArray.push(
                        {
                            label: `${user.firstName} ${user.lastName}`,
                            value: user.userId
                        }
                    );
                } else {
                    customersArray.push(
                        {
                            label: `${user.firstName} ${user.lastName}`,
                            value: user.userId
                        }
                    );
                };
            });
        };

        employeesArray.push({ label: 'General Contractor', value: 'General Contractor' });

        setEmployees(employeesArray);
        setCustomers(customersArray);
    }, [getUsers]);

    const toggle = e => {
        setIsOpen(!isOpen);
        setErrors({});
    };

    const handleChange = e => {
        return;
    };

    const handleSubmit = e => {
        e.preventDefault();
        return;
    };

    const changeHidden = location => {
        if(location.pathname === '/e_dashboard/workorders' || location.pathname === '/e_dashboard/workorders/') {
            setHidden(true);
        } else {
            setHidden(false);
        };
    };

    return (
        <div className={hidden ? "add-work-order-div": ""}>
            {hidden ? <Button color="success" onClick={toggle}>Create Work Order</Button> : null}
            <Modal isOpen={isOpen} toggle={toggle}>
                <ModalHeader toggle={toggle}>Create Work Order</ModalHeader>
                <ModalBody>
                    <Form onSubmit={handleSubmit}>
                        <FormGroup>
                            <Label for="customer">Customer</Label>
                            <Select options={customers} />
                        </FormGroup>
                        <div className="datepicker-div">
                            <FormGroup>
                                <Label for="date">Date</Label>
                                <Input type="date" name="date" id="date" placeholder="date placeholder" />
                            </FormGroup>
                            <FormGroup>
                                <Label for="time">Time</Label>
                                <Input type="time" name="time" id="time" placeholder="time placeholder" />
                            </FormGroup>
                        </div>
                        <FormGroup>
                            <Label for="serviceDescription">Service Description</Label>
                            <Input type="text" name="serviceDescription" id="serviceDescription" />
                        </FormGroup>
                        <FormGroup>
                            <Label for="serviceType">Service Type</Label>
                            <Select isMulti options={serviceTypes.map(type => ( { label: type, value: type } ))} />
                        </FormGroup>
                        <FormGroup>
                            <Label for="crewMembers">Crew Members</Label>
                            <Select isMulti options={employees} />
                        </FormGroup>
                        <FormGroup>
                            <Label for="prevJobNumber">Previous Job Number</Label>
                            <Input type="text" name="prevJobNumber" id="prevJobNumber" />
                        </FormGroup>
                        <FormGroup>
                            <Label for="notes">Notes</Label>
                            <Input type="textarea" name="notes" id="notes" />
                        </FormGroup>
                        <FormGroup>
                            <Label for="officeNotes">Office Notes</Label>
                            <Input type="textarea" name="officeNotes" id="officeNotes" />
                        </FormGroup>
                        <FormGroup>
                            <Label for="quote">Quote</Label>
                            <Input type="file" name="file" id="file" />
                        </FormGroup>
                    </Form>
                </ModalBody>
                <ModalFooter>
                    <Button color="success" type="submit" onClick={handleSubmit}>Submit</Button>
                </ModalFooter>
            </Modal>
        </div>
    );
};

export default AddWorkOrderBtn;

const serviceTypes = [
    "Install",
    "Service",
    "Training",
    "Inspection",
    "Warranty"
];
