import React, { useState, useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { useLocation } from 'react-router-dom';
import {
    Form,
    FormGroup,
    Label,
    Input,
    Button,
    Modal,
    ModalHeader,
    ModalBody,
    ModalFooter
} from 'reactstrap';
import EmployeeNavbar from './EmployeeNavbar';
import EmployeeSidebar from './EmployeeSidebar';
import Footer from './Footer';
import { getWorkOrder } from '../../actions/getWorkOrder';

import '../../styles/employee-dashboard.scss';

const WorkOrderPage = () => {
    const [workOrder, setWorkOrder] = useState({});
    const [customer, setCustomer] = useState({});
    const [employees, setEmployees] = useState([]);
    const getWorkOrderFromState = useSelector(state => state.getWorkOrderReducer.workOrder);
    const getUsers = useSelector(state => state.getWorkOrderReducer.users);
    const [isOpenServiceModal, setIsOpenServiceModal] = useState(false);
    const dispatch = useDispatch();
    const location = useLocation();

    useEffect(() => {
        const pathArray = location.pathname.split('/');
        const jobId = pathArray[pathArray.length - 1];
        dispatch(getWorkOrder(jobId));
    }, []);

    useEffect(() => {
        if(getWorkOrder) {
            setWorkOrder(getWorkOrderFromState);
        };
    }, [getWorkOrderFromState]);

    useEffect(() => {
        let crewMembers = [];

        if(getUsers) {
            getUsers.forEach(user => {
                if(user.userType === 'customer') {
                    setCustomer(user);
                } else {
                    crewMembers.push(user);
                };
            });
            setEmployees(crewMembers);
        }
    }, [getUsers]);

    const typeConverter = types => {
        if(types) {
            if(types.length === 1) {
                return types;
            } else {
                return types.join(', ');
            };
        };
    };

    const dateConverter = date => {
        if(date) {
            const dateArray = date.split('-');
            return `${dateArray[1]}/${dateArray[2]}/${dateArray[0]}`;
        };
    };

    const timeConverter = time => {
        if(time) {
            const timeArray = time.split(':');
            const hour = parseInt(timeArray[0]);
            let m;

            if(hour <= 11) {
                m = 'AM';
            } else {
                m = 'PM';
            };

            if(hour <= 12) {
                const newHour = hour.toString();
                timeArray[0] = newHour;
            } else {
                const newHour = (hour - 12).toString();
                timeArray[0] = newHour;
            };

            return `${timeArray[0]}:${timeArray[1]} ${m}`;
        };
    };

    const crewConverter = members => {
        let crewNames = [];

        if(members) {
            members.forEach(member => {
                crewNames.push(`${member.firstName} ${member.lastName}`);
            });

            if(crewNames.length === 1) {
                return crewNames;
            } else {
                return crewNames.join(', ');
            };
        };
    };

    const toggleServiceModal = () => {
        setIsOpenServiceModal(!isOpenServiceModal);
    };

    return (
        <div>
            <EmployeeNavbar />
            <div className="work-order-page">
                <EmployeeSidebar />
                {workOrder ? <div className="work-order-page-content">
                    <h1>{`Work Order #${workOrder.jobNumber}`}</h1>
                    <Form className="work-order-info">
                        <FormGroup>
                            <Label for="customerName">Customer Name</Label>
                            <Input disabled value={`${customer.firstName} ${customer.lastName}`} />
                        </FormGroup>
                        <FormGroup>
                            <Label for="customerAddress">Customer Address</Label>
                            <Input disabled value={`${customer.address}, ${customer.city}, ${customer.state} ${customer.zipcode}`} />
                        </FormGroup>
                        <div className="service-div">
                            <FormGroup className="description-input">
                                <Label for="serviceDescription">Service Description</Label>
                                <Input disabled value={workOrder.serviceDescription} />
                            </FormGroup>
                            <FormGroup className="type-input">
                                <Label for="serviceType">Service Type(s)</Label>
                                <Input disabled value={typeConverter(workOrder.serviceType)} />
                            </FormGroup>
                        </div>
                        <div className="date-div">
                            <FormGroup>
                                <Label for="serviceDate">Service Date</Label>
                                <Input disabled value={dateConverter(workOrder.serviceDate)} />
                            </FormGroup>
                            <FormGroup>
                                <Label for="serviceTime">Service Time</Label>
                                <Input disabled value={timeConverter(workOrder.serviceTime)} />
                            </FormGroup>
                        </div>
                        <FormGroup>
                            <Label for="crewMembers">Crew Members</Label>
                            <Input disabled value={crewConverter(employees)} />
                        </FormGroup>
                        <div className="button-div">
                            <FormGroup>
                                <Button outline color="success">Add Service</Button>
                            </FormGroup>
                            <div className="secondary-button-div">
                                <FormGroup>
                                    <Button outline color="info">Add Inspection</Button>
                                </FormGroup>
                                <FormGroup>
                                    <Button outline color="warning" className="training-button">Add Training</Button>
                                </FormGroup>
                            </div>
                        </div>
                        <FormGroup>
                            <Label for="notes">Notes</Label>
                            <Input type="textarea" value={workOrder.notes ? workOrder.notes : null} />
                        </FormGroup>
                        <FormGroup>
                            <Label for="officeNotes">Office Notes</Label>
                            <Input type="textarea" value={workOrder.officeNotes ? workOrder.officeNotes : null} />
                        </FormGroup>
                        <FormGroup className="submit-button">
                            <Button color="success">Submit for Signature</Button>
                        </FormGroup>
                    </Form>
                </div> : null}
                <Modal isOpen={isOpenServiceModal} toggle={toggleServiceModal}>
                    <ModalHeader toggle={toggleServiceModal}>Add Service</ModalHeader>
                    <ModalBody>
                        <Form>
                            <FormGroup>
                                <Label>What work was done?</Label>
                                <Input type="textarea" />
                            </FormGroup>
                            <FormGroup>
                                <Button>Add Part Used</Button>
                                <Button>Add Part Needed</Button>
                            </FormGroup>
                        </Form>
                    </ModalBody>
                </Modal>
            </div>
            <Footer />
        </div>
    );
};

export default WorkOrderPage;
