import React, { useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import CurrentEmployeesContent from './CurrentEmployeesContent';
import { getEmployees } from '../../actions/getEmployees';

const EmployeesContent = () => {
    const all = useSelector(state => state.employeesToggleReducer.all);
    const dispatch = useDispatch();

    useEffect(() => {
        dispatch(getEmployees());
    }, []);

    return (
        <div className="employees-content-page">
            {all ? <h1>Employees - All</h1> : <CurrentEmployeesContent />}
        </div>
    );
};

export default EmployeesContent;
